        System.out.println("input  | 000 | 001 | 010 | 011 | 100 | 101 | 110 | 111");
        System.out.println("------------------------------------------------------");
        System.out.println("output | 110 | 101 | 001 | 000 | 011 | 010 | 111 | 100\n");
        System.out.println("In terms of hexadecimal notation, the S-box is given by: ");
        System.out.println("input  | 0 | 1 | 2 | 3 | 4 | 5 | 6 | 7");
        System.out.println("--------------------------------------");
        System.out.println("output | 6 | 5 | 1 | 0 | 3 | 2 | 7 | 4");

        int[] sbox = {0x6, 0x5, 0x1, 0x0, 0x3, 0x2, 0x7, 0x4};
        int[][] nlat = new int [8][8];

        for(int a = 0; a < 8; a++) {
            for(int b = 0; b < 8; b++) {
                nlat[a][b] = lat(sbox, a, b) - 4;
            }
        }

        // print the normalized linear approximation table
        System.out.println("\nNormalized Linear Approximation Table:");
        for(int i = 0; i < 8; i++) {
            for(int j = 0; j < 8; j++) {
                System.out.print(nlat[i][j] + "\t");
            }
            System.out.println();
        }
    }

    public static int lat(int[] sbox, int a, int b) {
        int count = 0;
        for(int x = 0; x < 8; x++) {
            int fx = sbox[x];
            int input_mask = scalar_product(a, x);
            int output_mask = scalar_product(b, fx);
            if (input_mask == output_mask) {
                count++;
            }
        }
        return count;
    }

    public static int scalar_product(int a, int b) {
        int product = 0;
        for (int i = 0; i < 3; i++) {
            int a_bit = (a >> i) & 1;
            int b_bit = (b >> i) & 1;
            product ^= (a_bit * b_bit);
        }
        return product;
    }
}
