def remove_node(self, id):
        '''
        removes the node with the given id
        '''
        if id in self.inputs:
            self.inputs.remove(id)
        if id in self.outputs:
            self.outputs.remove(id)
        if id not in self.nodes:
            raise ValueError(f"node id invalide :{id}")
        node = self.nodes[id]
        for parent_id in node.get_parents_ids():
            p = self.nodes[parent_id]
            p.remove_child_id(id)
        for child_id in node.get_children_ids():
            c = self.nodes[child_id]
            c.remove_parent_id(id)
        del self.nodes[id]
    #ok

    def remove_edge(self, src, tgt):
        '''
        removes the edge (src,tgt)
        '''
        src_node = self.get_node_by_id(src)
        tgt_node = self.get_node_by_id(tgt)
        src_node.remove_child_id(tgt)
        tgt_node.remove_parent_id(src)
    #ok

    def turn(self):
        '''
        reverse the direction of ALL the edges
        '''
        new_dag = self.copy()
        for n1 in self.nodes.values():
            for c_id in n1.get_children_ids():
                new_dag.remove_edge(n1.get_id(), c_id)
                new_dag.add_edge(c_id, n1.get_id())
        return new_dag
    #ok

    def reverse(self):
        '''
        creates a REVERSED copy of the graph
        '''
        new_dag = self.copy()
        for node in new_dag.nodes.values():
            node.parents, node.children = node.children, node.parents
        new_dag.inputs, new_dag.outputs = new_dag.outputs, new_dag.inputs
        return new_dag

    def new_id(self):
        '''
        returns a new, unused id in the graph
        '''
        ids = set(self.get_node_ids()) | set(self.get_input_ids()) | set(self.get_output_ids())
        for i in range(1, len(ids)+1):
            if i not in ids:
                return i
        return len(ids)+1

    def make_dot(self):
        '''
        creates a pydot.Digraph object representing the directed graph
        '''
        dot = pydot.Dot(graph_type='digraph')
        nodes = {}
        for n in self.nodes.values():
            label = n.get_label()
            if label == '':
                label = str(n.get_id())
            nodes[n.get_id()] = pydot.Node(str(n.get_id()), label=label)
            for c_id, c_mult in n.children.items():
                if c_id in nodes:
                    tgt_node = nodes[c_id]
                else:
                    label = self.nodes[c_id].get_label()
                    if label == '':
                        label = str(c_id)
                    tgt_node = pydot.Node(str(c_id), label=label)
                    nodes[c_id] = tgt_node
                src_node = nodes[n.get_id()]
                for _ in range(c_mult):
                    dot.add_edge(pydot.Edge(src_node, tgt_node))
        for i_id in self.get_input_ids():
            if i_id not in nodes:
                nodes[i_id] = pydot.Node(str(i_id), label='input', shape='box')
            dot.add_edge(pydot.Edge(nodes[i_id], nodes[self.get_node_by_id(i_id).get_children_ids()[0]]))
        for o_id in self.get_output_ids():
            if o_id not in nodes:
                nodes[o_id] = pydot.Node(str(o_id), label='output', shape='box')
            dot.add_edge(pydot.Edge(nodes[self.get_node_by_id(o_id).get_parents_ids()[0]], nodes[o_id]))
        return dot

    def draw(self):
        '''
        draws the directed graph in jupyter
        '''
        return self.make_dot().create_jupyter_notebook_svg()

