                    ImageReader.newInstance(PREVIEW_WIDTH, PREVIEW_HEIGHT, ImageFormat.YUV_420_888, 3);
            imageReader.setOnImageAvailableListener(new ImageReader.OnImageAvailableListener() {
                @Override
                public void onImageAvailable(ImageReader reader) {
                    Image image = reader.acquireLatestImage();
                    if (image != null) {
                        if (!imageBitmapInitialized) {
                            imageBitmap =
                                    Bitmap.createBitmap(PREVIEW_WIDTH, PREVIEW_HEIGHT, Bitmap.Config.ARGB_8888);
                        }
                        yuvConverter.yuvToRgb(image, imageBitmap);
                        // Create rotated version for portrait display
                        Matrix rotateMatrix = new Matrix();
                        rotateMatrix.postRotate(90.0f);

                        Bitmap rotatedBitmap = Bitmap.createBitmap(
                                imageBitmap, 0, 0, PREVIEW_WIDTH, PREVIEW_HEIGHT,
                                rotateMatrix, false
                        );
                        processImage(rotatedBitmap);
                        image.close();
                    }
                }
            }, imageReaderHandler);

            Surface surface = imageReader.getSurface();
            session = createSession(Arrays.asList(surface));
            CameraCaptureRequest cameraRequest = camera.createCaptureRequest(CameraDevice.TEMPLATE_PREVIEW);
            if (cameraRequest != null) {
                cameraRequest.addTarget(surface);
            }
            cameraRequest.build();
            if (session != null) {
                session.setRepeatingRequest(cameraRequest.build(), null, null);
            }
        } catch (CameraAccessException e) {
            // handle exception
            e.printStackTrace();
        }
    }
