        //            e.printStackTrace()
        //        }
        //syncTimeMapper.updateSyncTime(header.orgId,type, fromTo)
    }

    public String getAccessToken(HeaderDTO header) {
        RefreshTokenResquest request = new RefreshTokenResquest(
                appKey,
                appSecret,
                String.valueOf(header.getOrgId())
        );
        RefreshTokenResponse response = v3OpenClient.getAccessToken(request);
        return response.getData().getToken();
    }

    public String getSyncTime(String type, HeaderDTO header) {
        String syncTime = v3OpenClient.getSyncTime(type, header);
        return Objects.requireNonNull(syncTime, "获取当前时间失败");
    }

    public HeaderDTO getHeader(String type) {
        return new HeaderDTO(
                Long.parseLong(orgId),
                Long.parseLong(userId),
                username,
                Long.parseLong(orgId)
        );
    }

