C++代码：

```c++
#include <jni.h>
#include <oboe/Oboe.h>

JavaVM* gJvm = nullptr; // Kotlin虚拟机环境
jobject gJavaObj = nullptr; // Kotlin对象的全局引用
jmethodID gJavaMethod = nullptr; // Kotlin方法的ID

class MyOboeCallback : public oboe::AudioStreamDataCallback {
    oboe::DataCallbackResult onAudioReady(oboe::AudioStream* stream, void* audio_data,
        int32_t num_frames) override {
        jbyte* buffer = static_cast<jbyte*>(audio_data);
        int buffer_len = num_frames * stream->getBytesPerFrame();

        // 获取JNIEnv指针
        JNIEnv* env = nullptr;
        gJvm->AttachCurrentThread(&env, nullptr);

        // 把Audio数据传递到Kotlin层
        jbyteArray audio_buffer = env->NewByteArray(buffer_len);
        env->SetByteArrayRegion(audio_buffer, 0, buffer_len, reinterpret_cast<jbyte*>(buffer));
        env->CallVoidMethod(gJavaObj, gJavaMethod, audio_buffer, buffer_len);

        // 释放局部引用
        env->DeleteLocalRef(audio_buffer);

        // 释放JNIEnv指针
        gJvm->DetachCurrentThread();

        return oboe::DataCallbackResult::Continue;
    }
};

JNIEXPORT jint JNICALL JNI_OnLoad(JavaVM* vm, void* reserved) {
    JNIEnv* env;
    if (vm->GetEnv((void**) &env, JNI_VERSION_1_6) != JNI_OK) {
        return JNI_ERR;
    }

    // 根据包名和类名获取Java类的引用
    jclass java_cls = env->FindClass("com/example/MainActivity");
    if (java_cls == nullptr) {
        return JNI_ERR;
    }

    // 获取Kotlin虚拟机环境
    gJvm = vm;

    // 保存Kotlin对象的全局引用
    jmethodID constructor = env->GetMethodID(java_cls, "<init>", "()V");
    gJavaObj = env->NewGlobalRef(env->NewObject(java_cls, constructor));

    // 根据方法名和签名获取Java方法的ID
    gJavaMethod = env->GetMethodID(java_cls, "onAudioReady", "([BI)V");
    if (gJavaMethod == nullptr) {
        return JNI_ERR;
    }

    return JNI_VERSION_1_6;
}

extern "C" JNIEXPORT void JNICALL
Java_com_example_MyOboeCallback_native_1createStream(JNIEnv *env, jobject thiz, jint sample_rate,
        jint channel_count, jint data_format, jint performance_mode, jint sharing_mode) {
    oboe::AudioStreamBuilder builder;
    builder.setChannelCount(channel_count)
            .setFormat(static_cast<oboe::AudioFormat>(data_format))
            .setPerformanceMode(static_cast<oboe::PerformanceMode>(performance_mode))
            .setSharingMode(static_cast<oboe::SharingMode>(sharing_mode))
            .setSampleRate(sample_rate)
            .setDataCallback(new MyOboeCallback())
            .setErrorCallback(oboe::ErrorCallback::defaultErrorCallback);
    oboe::AudioStream *stream;
    builder.openStream(&stream);
    stream->start();
}
