```csharp
using System;
using System.IO;
using Microsoft.Office.Interop.Word;

namespace WordExport
{
    class Program
    {
        static void Main(string[] args)
        {
            string sourcePath = @"C:\source\";
            string targetPath = @"C:\target\";
            string[] files = Directory.GetFiles(sourcePath, "*.docx");

            Application word = new Application();
            word.Visible = false;

            foreach (string file in files)
            {
                Document doc = word.Documents.Open(file);

                string filename = Path.GetFileNameWithoutExtension(file);
                string targetFile = targetPath + filename + ".pdf";

                doc.ExportAsFixedFormat(targetFile, WdExportFormat.wdExportFormatPDF);

                doc.Close();
            }

            word.Quit();
        }
    }
}
```

