int zc_close(zc_file *file) {
    if (!file) {
        return -1;
    }

    // Synchronize memory file to disk.
    msync(file->addr, file->size, MS_SYNC);

    // Unmap the file from memory.
    munmap(file->addr, file->size);

    // Close the file descriptor.
