off_t zc_lseek(zc_file *file, long offset, int whence) {
    if (!file) {
        return -1;
    }

    off_t new_offset;
    switch (whence) {
        case SEEK_SET:
            new_offset = offset;
            break;
        case SEEK_CUR:
            new_offset = file->offset + offset;
            break;
        case SEEK_END:
            new_offset = file->size + offset;
            break;
        default:
            return -1;
    }

    if (new_offset < 0 || new_offset > file->size) {
        return -1;
    }

    file->offset = new_offset;
    return file->offset;
}

int zc_copyfile(const char *source, const char *dest) {
    size_t buffer_size = 4096;
    zc_file *src = zc_open(source);

    if (!src) {
        return -1;
    }

    zc_file *dst = zc_open(dest);
    if (!dst) {
        zc_close(src);
        return -1;
    }

    while (1) {
        size_t read_size = buffer_size;
        const char *src_buffer = zc_read_start(src, &read_size);

        if (!src_buffer || read_size == 0)
            break;

        char *dst_buffer = zc_write_start(dst, read_size);
        if (!dst_buffer) {
            zc_close(src);
            zc_close(dst);
            return -1;
        }

        // Copy data from src to dst.
        memcpy(dst_buffer, src_buffer, read_size);

        zc_read_end(src);
        zc_write_end(dst);
    }

    zc_close(src);
    zc_close(dst);

    return 0;
}

