```c
#include "zc_io.h"

struct zc_file {
    void *ptr;
    off_t offset;
    size_t size;
    int fd;
    pthread_mutex_t mutex;
    unsigned int readers;
    pthread_cond_t readers_cond;
    pthread_cond_t writer_cond;
    int writing;
};

zc_file *zc_open(const char *path) {
    int fd = open(path, O_CREAT | O_RDWR, 0666);
    if (fd == -1) {
        perror("open");
        return NULL;
    }

    struct stat st;
    fstat(fd, &st);
    size_t size = st.st_size;

    void *ptr = mmap(NULL, size, PROT_READ | PROT_WRITE, MAP_PRIVATE, fd, 0);
    if (ptr == MAP_FAILED) {
        perror("mmap");
        close(fd);
        return NULL;
    }

    zc_file *file = (zc_file *)malloc(sizeof(zc_file));
    file->ptr = ptr;
    file->offset = 0;
    file->size = size;
    file->fd = fd;
    pthread_mutex_init(&(file->mutex), NULL);
    pthread_cond_init(&(file->readers_cond), NULL);
    pthread_cond_init(&(file->writer_cond), NULL);
    file->readers = 0;
    file->writing = 0;

    return file;
}

int zc_close(zc_file *file) {
    if (munmap(file->ptr, file->size) == -1) {
        perror("munmap");
        return -1;
    }

    close(file->fd);
    pthread_mutex_destroy(&(file->mutex));
    pthread_cond_destroy(&(file->readers_cond));
    pthread_cond_destroy(&(file->writer_cond));
    free(file);

    return 0;
}

const char *zc_read_start(zc_file *file, size_t *size) {
    pthread_mutex_lock(&(file->mutex));
    file->readers++;
    while (file->writing) {
        pthread_cond_wait(&(file->readers_cond), &(file->mutex));
    }

    if (file->offset + *size > file->size) {
        *size = file->size - file->offset;
    }

    const char *data = file->ptr + file->offset;
    file->offset += *size;

    pthread_mutex_unlock(&(file->mutex));

    return data;
}

void zc_read_end(zc_file *file) {
    pthread_mutex_lock(&(file->mutex));
    file->readers--;
    if (file->readers == 0) {
        pthread_cond_signal(&(file->writer_cond));
    }
    pthread_mutex_unlock(&(file->mutex));
}

char *zc_write_start(zc_file *file, size_t size) {
    pthread_mutex_lock(&(file->mutex));
    while (file->writing || file->readers) {
        if (file->readers) {
            pthread_cond_broadcast(&(file->readers_cond));
        }
        pthread_cond_wait(&(file->writer_cond), &(file->mutex));
    }
    file->writing = 1;

    if (file->offset + size > file->size) {
        file->size = file->offset + size;
        if (ftruncate(file->fd, file->size) == -1) {
            perror("ftruncate");
            return NULL;
        }
    }

    char *data = file->ptr + file->offset;
    file->offset += size;

    pthread_mutex_unlock(&(file->mutex));

    return data;
}

void zc_write_end(zc_file *file) {
    pthread_mutex_lock(&(file->mutex));
    file->writing = 0;
    pthread_cond_broadcast(&(file->readers_cond));
    pthread_mutex_unlock(&(file->mutex));
}

off_t zc_lseek(zc_file *file, long offset, int whence) {
    pthread_mutex_lock(&(file->mutex));
    while (file->writing || file->readers) {
        if (file->readers) {
            pthread_cond_broadcast(&(file->readers_cond));
        }
        pthread_cond_wait(&(file->writer_cond), &(file->mutex));
    }

    switch (whence) {
        case SEEK_SET:
            file->offset = offset;
            break;
        case SEEK_CUR:
            file->offset += offset;
            break;
        case SEEK_END:
            file->offset = file->size + offset;
            break;
        default:
            perror("Invalid whence");
            return -1;
    }
    off_t new_offset = file->offset;

    pthread_mutex_unlock(&(file->mutex));

    return new_offset;
}

int zc_copyfile(const char *source, const char *dest) {
    zc_file *src_file = zc_open(source);
    zc_file *dst_file = zc_open(dest);

    if (src_file == NULL || dst_file == NULL) {
        return -1;
    }

    off_t src_offset = 0;
    off_t dst_offset = 0;

    while (src_offset < src_file->size) {
        size_t chunk_size = src_file->size - src_offset;
        const char *src_data = zc_read_start(src_file, &chunk_size);
        if (src_data == NULL) {
            return -1;
        }
        src_offset = zc_lseek(src_file, chunk_size, SEEK_CUR);

        char *dst_data = zc_write_start(dst_file, chunk_size);
        if (dst_data == NULL) {
            return -1;
        }
        memcpy(dst_data, src_data, chunk_size);
        zc_write_end(dst_file);
        dst_offset = zc_lseek(dst_file, chunk_size, SEEK_CUR);
    }

    zc_close(src_file);
    zc_close(dst_file);

    return 0;
}
```

