    WNDCLASS wc = {0};
    wc.lpfnWndProc = WndProc;
    wc.hInstance = hInstance;
    wc.hbrBackground = CreateSolidBrush(RGB(255, 255, 255));
    wc.lpszClassName = "MyWndClass";
    RegisterClass(&wc);

    HWND hwnd = CreateWindow(
        "MyWndClass",
        "Draggable and Transparent Window",
        WS_OVERLAPPEDWINDOW,
        CW_USEDEFAULT, CW_USEDEFAULT, 400, 300,
        NULL, NULL, hInstance, NULL
    );
    SetWindowLong(hwnd, GWL_EXSTYLE, GetWindowLong(hwnd, GWL_EXSTYLE) | WS_EX_LAYERED);

    DragAcceptFiles(hwnd, TRUE);

    ShowWindow(hwnd, nCmdShow);

    MSG msg = {0};
    while (GetMessage(&msg, NULL, 0, 0)) {
        TranslateMessage(&msg);
        DispatchMessage(&msg);
    }
    return msg.wParam;
}

LRESULT CALLBACK WndProc(HWND hwnd, UINT msg, WPARAM wParam, LPARAM lParam) {
    static POINT offset;
    static BOOL dragging = FALSE;
    switch (msg) {
        case WM_DESTROY:
            PostQuitMessage(0);
            break;
        case WM_DROPFILES: {
            HDROP hDrop = (HDROP)wParam;
            DWORD count = DragQueryFile(hDrop, 0xFFFFFFFF, NULL, 0);
            for (DWORD i = 0; i < count; i++) {
                CHAR filename[MAX_PATH];
                DragQueryFile(hDrop, i, filename, MAX_PATH);
                MessageBox(NULL, filename, "Dropped file", MB_OK | MB_ICONINFORMATION);
            }
            DragFinish(hDrop);
            break;
        }
        case WM_LBUTTONDOWN:
            offset.x = GET_X_LPARAM(lParam);
            offset.y = GET_Y_LPARAM(lParam);
            dragging = TRUE;
            SetLayeredWindowAttributes(hwnd, 0, 200, LWA_ALPHA);
            SetCursor(LoadCursor(NULL, IDC_SIZEALL));
            break;
        case WM_MOUSEMOVE:
            if (dragging) {
                int x = GET_X_LPARAM(lParam) - offset.x;
                int y = GET_Y_LPARAM(lParam) - offset.y;
                SetWindowPos(hwnd, NULL, x, y, 0, 0, SWP_NOSIZE | SWP_NOZORDER | SWP_NOACTIVATE);
            }
            break;
        case WM_LBUTTONUP:
            dragging = FALSE;
            SetLayeredWindowAttributes(hwnd, 0, 255, LWA_ALPHA);
            SetCursor(LoadCursor(NULL, IDC_ARROW));
            break;
        default:
            return DefWindowProc(hwnd, msg, wParam, lParam);
    }
    return 0;
}
```

