OleDbParameter applianceParam = new OleDbParameter("@Appliance", OleDbType.VarChar);
applianceParam.Value = appliance;
OleDbParameter powerUsageParam = new OleDbParameter("@PowerUsage", OleDbType.VarChar);
powerUsageParam.Value = powerUsage;
OleDbParameter typicalUsageParam = new OleDbParameter("@TypicalUsage", OleDbType.VarChar);
typicalUsageParam.Value = typicalUsage;
OleDbParameter annualCostsParam = new OleDbParameter("@AnnualCosts", OleDbType.VarChar);
annualCostsParam.Value = annualCosts;

cmd.Parameters.Add(applianceParam);
cmd.Parameters.Add(powerUsageParam);
cmd.Parameters.Add(typicalUsageParam);
cmd.Parameters.Add(annualCostsParam);

int rowsAffected = cmd.ExecuteNonQuery();

if (rowsAffected == 0)
{
    query = "INSERT INTO ApplianceDBLIST (Appliance, [Power Usage], [Typical Usage], [Estimated annual running costs]) VALUES (@Appliance, @PowerUsage, @TypicalUsage, @AnnualCosts)";
    cmd = new OleDbCommand(query, con);

    cmd.Parameters.Add(applianceParam);
    cmd.Parameters.Add(powerUsageParam);
    cmd.Parameters.Add(typicalUsageParam);
    cmd.Parameters.Add(annualCostsParam);

    cmd.ExecuteNonQuery();
}
```

After applying those changes, your code should work as expected without throwing the OleDbException. Let me know if you need further help or if you encounter any other issues.

