```csharp
private void saveButton_Click(object sender, EventArgs e)
{
    con.Open();

    foreach (DataGridViewRow row in dataGridView1.Rows)
    {
        if (!row.IsNewRow)
        {
            if (row.Cells[0].Value != null && !string.IsNullOrEmpty(row.Cells[0].Value.ToString()))
            {
                string appliance = row.Cells[0].Value.ToString();
                string powerUsage = row.Cells[1].Value.ToString();
                string typicalUsage = row.Cells[2].Value.ToString();
                string annualCosts = row.Cells[3].Value.ToString();

                string selectQuery = "SELECT COUNT(*) FROM ApplianceDBLIST WHERE Appliance = ?";
                cmd = new OleDbCommand(selectQuery, con);
                cmd.Parameters.Add(new OleDbParameter("@Appliance", appliance));

                int existingRowCount = (int)cmd.ExecuteScalar();

                string query = "";
                if (existingRowCount > 0)
                {
                    query = "UPDATE ApplianceDBLIST SET [Power Usage] = ?, [Typical Usage] = ?, [Estimated annual running costs] = ? WHERE Appliance = ?";
                }
                else
                {
                    query = "INSERT INTO ApplianceDBLIST (Appliance, [Power Usage], [Typical Usage], [Estimated annual running costs]) VALUES (?, ?, ?, ?)";
                }

                cmd = new OleDbCommand(query, con);

                OleDbParameter powerUsageParam = new OleDbParameter("@PowerUsage", OleDbType.VarChar);
                powerUsageParam.Value = powerUsage;
                OleDbParameter typicalUsageParam = new OleDbParameter("@TypicalUsage", OleDbType.VarChar);
                typicalUsageParam.Value = typicalUsage;
                OleDbParameter annualCostsParam = new OleDbParameter("@AnnualCosts", OleDbType.VarChar);
                annualCostsParam.Value = annualCosts;
                OleDbParameter applianceParam = new OleDbParameter("@Appliance", OleDbType.VarChar);
                applianceParam.Value = appliance;

                cmd.Parameters.Add(powerUsageParam);
                cmd.Parameters.Add(typicalUsageParam);
                cmd.Parameters.Add(annualCostsParam);
                cmd.Parameters.Add(applianceParam);

                cmd.ExecuteNonQuery();
            }
        }
