```java
import io.github.resilience4j.circuitbreaker.CircuitBreaker;
import io.github.resilience4j.circuitbreaker.CircuitBreakerConfig;

@Service
public class UserService {

    private final JdbcTemplate jdbcTemplate;
    private final CircuitBreaker circuitBreaker;

    public UserService(JdbcTemplate jdbcTemplate, CircuitBreakerRegistry circuitBreakerRegistry) {
        this.jdbcTemplate = jdbcTemplate;

        CircuitBreakerConfig circuitBreakerConfig = CircuitBreakerConfig.custom()
              .failureRateThreshold(50)
              .waitDurationInOpenState(Duration.ofMillis(1000))
              .ringBufferSizeInHalfOpenState(2)
              .ringBufferSizeInClosedState(2)
              .build();

        this.circuitBreaker = circuitBreakerRegistry.circuitBreaker("userService", circuitBreakerConfig);
    }

    public List<User> findUsersByName(String lastName) {
        return circuitBreaker.executeSupplier(() -> jdbcTemplate.query("SELECT * FROM users WHERE last_name = ?",
            new Object[]{lastName},
            (resultSet, i) -> new User(resultSet.getLong("id"),
                resultSet.getString("first_name"),
                resultSet.getString("last_name"))));
    }
}
```

