```java
import org.springframework.http.HttpStatus;
import org.springframework.http.ResponseEntity;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestParam;
import org.springframework.web.multipart.MultipartFile;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.LinkedList;
import java.util.List;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.Future;
import java.util.stream.Collectors;

@Controller
public class FileController {

    @GetMapping("/")
    public String index() {
        return "index";
    }

    @PostMapping("/checkUrls")
    public String checkUrls(@RequestParam("file") MultipartFile file, Model model) throws IOException {
        List<String> urls = new LinkedList<>();
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(file.getInputStream()))) {
            String line;
            while ((line = reader.readLine()) != null) {
                urls.add(line);
            }
        }

        List<URLStatus> urlStatuses = checkUrlsStatus(urls);
        model.addAttribute("urlStatuses", urlStatuses);
        return "result";
    }

    private List<URLStatus> checkUrlsStatus(List<String> urls) {
        ExecutorService executorService = Executors.newFixedThreadPool(Runtime.getRuntime().availableProcessors());
        List<Future<URLStatus>> futures = new LinkedList<>();
        for (String url : urls) {
            futures.add(executorService.submit(() -> {
                try {
                    ResponseEntity<String> response = restTemplate.getForEntity(url, String.class);
                    if (response.getStatusCode() == HttpStatus.NOT_FOUND) {
                        return new URLStatus(url, true);
                    }
                } catch (Exception ex) {
                    // Handle exceptions if needed
                }
                return new URLStatus(url, false);
            }));
        }

        List<URLStatus> result = futures.stream().map(future -> {
            try {
                return future.get();
            } catch (Exception e) {
                throw new RuntimeException(e);
            }
        }).collect(Collectors.toList());

        executorService.shutdown();
        return result;
    }

    public static class URLStatus {
        private final String url;
        private final boolean is404;

        public URLStatus(String url, boolean is404) {
            this.url = url;
            this.is404 = is404;
        }

        public String getUrl() {
            return url;
        }

        public boolean is404() {
            return is404;
        }
    }
}
```

