        g2d.setColor(new Color(224, 100, 35));
        g2d.fillRoundRect(90, 200, 20, 35, 10, 10);
        g2d.fillRoundRect(150, 200, 20, 35, 10, 10);
        // 狗尾巴
        g2d.drawLine(170, 190, 210, 150);
        g2d.drawLine(210, 150, 240, 180);
        g2d.drawLine(240, 180, 265, 150);
        g2d.drawLine(265, 150, 290, 190);

        g2d.dispose();

        try {
            ImageIO.write(image, "png", new File("runningDog.png")); // 保存图片
        } catch (Exception e) {
            e.printStackTrace();
        }

        JFrame frame = new JFrame("DrawRunningDog");
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        JLabel label = new JLabel(new ImageIcon(image));
        frame.getContentPane().add(label, BorderLayout.CENTER);
        frame.pack();
        frame.setVisible(true);
    }
}
```

