```java
import javax.swing.*;
import java.awt.*;

public class DogDrawing {
    public static void main(String[] args) {
        JFrame frame = new JFrame("Dog Drawing");
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        frame.setSize(800, 600);

        DrawingPanel drawingPanel = new DrawingPanel();
        frame.add(drawingPanel);
        frame.setVisible(true);
    }
}

class DrawingPanel extends JPanel {
    @Override
    protected void paintComponent(Graphics g) {
        super.paintComponent(g);

        // Head
        g.drawOval(350, 200, 100, 100); // face
        g.fillOval(360, 220, 20, 10); // eyes
        g.fillOval(420, 220, 20, 10);

        g.drawArc(370, 250, 60, 40, 0, -180); // mouth

        g.drawLine(350, 200, 330, 185); // left ear
        g.drawLine(330, 185, 350, 150);

        g.drawLine(450, 200, 470, 185); // right ear
        g.drawLine(470, 185, 450, 150);

        // Body and tail
        g.drawOval(335, 300, 130, 100); // body
        g.drawLine(460, 350, 530, 320); // tail

        // Front legs
        g.drawLine(365, 400, 365, 500); // left front leg
        g.drawLine(365, 500, 355, 525);
        g.drawLine(355, 525, 375, 525);

        g.drawLine(435, 400, 435, 500); // right front leg
        g.drawLine(435, 500, 425, 525);
        g.drawLine(425, 525, 445, 525);

        // Back legs
        g.drawLine(300, 380, 310, 500); // left back leg
        g.drawLine(310, 500, 300, 525);
        g.drawLine(300, 525, 320, 525);

        g.drawLine(500, 380, 490, 500); // right back leg
        g.drawLine(490, 500, 505, 525);
        g.drawLine(480, 525, 505, 525);
    }
}
```

