class Cube {
    double side;

    public Cube(double side) {
        this.side = side;
    }

    public double getVolume() {
        return Math.pow(side, 3);
    }
}

class Box {
    double length;
    double width;
    double height;

    public Box(double length, double width, double height) {
        this.length = length;
        this.width = width;
        this.height = height;
    }

    public boolean canFit(Cube cube) {
        return length >= cube.side && width >= cube.side && height >= cube.side;
    }
}

public class Main {
    public static void main(String[] args) {
        Cube myCube = new Cube(5);
        Box myBox = new Box(10, 10, 10);

        if (myBox.canFit(myCube)) {
            System.out.println("Cube fits in the box.");
            System.out.println("Cube volume: " + myCube.getVolume());
        } else {
            System.out.println("Cube does not fit in the box.");
        }
    }
}
