```matlab
% Define the coefficient matrix A and the constant matrix B
A = [2 -3 1; 1 -1 -2; 3 1 -1];
B = [7;-2;0];

% Perform LU factorization
[L, U, P] = lu(A);

% Solve the system using forward and backward substitution
y = forward_substitution(L, P * B);
x = backward_substitution(U, y);

% Display the result
disp('The solution using LU factorization is:');
disp(x);

% Define the function for forward substitution
function y = forward_substitution(L, B)
    n = length(B);
    y = zeros(n, 1);

    for k = 1:n
        y(k) = B(k) - L(k, 1:k-1) * y(1:k-1);
    end
end

% Define the function for backward substitution
function x = backward_substitution(U, y)
    n = length(y);
    x = zeros(n, 1);

    for k = n:-1:1
        x(k) = (y(k) - U(k, k+1:n) * x(k+1:n)) / U(k, k);
    end
end
```

