<html>
  <head>
    <meta charset="UTF-8">
    <title>Display Data</title>
  </head>
  <body>
    <h1>Data from MySQL Database</h1>
    <div id="data"></div>
    <script>
      // Send an HTTP GET request to the servlet to fetch the data
      var xhr = new XMLHttpRequest();
      xhr.open('GET', '/your-web-app-name/data');
      xhr.onload = function() {
        if (xhr.status === 200) {
          // Output the data to the web page
          document.querySelector('#data').innerHTML = xhr.responseText;
        }
      };
      xhr.send();
    </script>
