import java.util.ArrayList;
import java.util.Random;

public class RandomWordSelector {
    private static final int NUM_WORDS_TO_SELECT = 10;

    public static void main(String[] args) {
        ArrayList<String> words = readWordsFromFile("words.txt");
        int numWords = words.size();
        System.out.println("Total words in file: " + numWords);

        if (numWords >= NUM_WORDS_TO_SELECT) {
            Random random = new Random();
            ArrayList<String> selectedWords = new ArrayList<>();

            for (int i = 0; i < NUM_WORDS_TO_SELECT; i++) {
                int index = random.nextInt(numWords);
                String word = words.get(index);
                selectedWords.add(word);
            }

            System.out.println("Randomly selected words:");
            System.out.println(selectedWords);
        }
        else {
            System.err.println("Cannot select " + NUM_WORDS_TO_SELECT + " words from file with " + numWords + " words.");
        }
    }

    private static ArrayList<String> readWordsFromFile(String fileName) {
        ArrayList<String> words = new ArrayList<>();
        try (BufferedReader br = new BufferedReader(new FileReader(fileName))) {
            String line;
            while ((line = br.readLine()) != null) {
                for (String word : line.split("\\s+")) {
                    words.add(word);
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
        return words;
    }
}
