```java
import java.io.File;
import java.util.ArrayList;
import java.util.Random;
import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.NodeList;

public class RandomWordSelector {

    private static final String XML_FILE = "words.xml";
    private static final int NUM_WORDS_TO_SELECT = 10;
    private static final String WORD_TAG = "wordx";

    public static void main(String[] args) {
        ArrayList<String> words = readWordsFromXMLFile();
        int numWords = words.size();
        System.out.println("Total words of importance in file: " + numWords);

        if (numWords >= NUM_WORDS_TO_SELECT) {
            Random random = new Random();
            ArrayList<String> selectedWords = new ArrayList<>();

            for (int i = 0; i < NUM_WORDS_TO_SELECT; i++) {
                int index = random.nextInt(numWords);
                String word = words.get(index);
                selectedWords.add(word);
                words.remove(index); // to avoid duplicates
                numWords--;
            }

            System.out.println("Randomly selected words:");
            System.out.println(selectedWords);
        } else {
            System.err.println("Cannot select " + NUM_WORDS_TO_SELECT + " words from file with " + numWords + " words.");
        }
    }

    private static ArrayList<String> readWordsFromXMLFile() {
        ArrayList<String> words = new ArrayList<>();
        try {
            File xmlFile = new File(XML_FILE);
            DocumentBuilderFactory dbFactory = DocumentBuilderFactory.newInstance();
            DocumentBuilder dBuilder = dbFactory.newDocumentBuilder();
            Document doc = dBuilder.parse(xmlFile);

            NodeList nodeList = doc.getElementsByTagName(WORD_TAG);
            for (int i = 0; i < nodeList.getLength(); i++) {
                Element element = (Element) nodeList.item(i);
                String word = element.getTextContent().trim();
                words.add(word);
            }
        } catch (Exception e) {
            System.err.println("Error reading XML file: " + e.getMessage());
        }
        return words;
    }
}
```

