import javax.imageio.ImageIO;

public class RandomPixelSelector {

    public static void main(String[] args) {
        File jpegFile = new File("image.jpg");
        int width = getImageWidth(jpegFile);
        int height = getImageHeight(jpegFile);
        System.out.println("Image dimensions: " + width + "x" + height);

        Random random = new Random();
        int x = random.nextInt(width);
        int y = random.nextInt(height);
        System.out.println("Random pixel coordinates: (" + x + "," + y + ")");

        try {
            BufferedImage image = ImageIO.read(jpegFile);
            int rgb = image.getRGB(x, y);
            int red = (rgb >> 16) & 0xFF;
            int green = (rgb >> 8) & 0xFF;
            int blue = (rgb) & 0xFF;
            System.out.println("Pixel color (RGB): (" + red + "," + green + "," + blue + ")");
        } catch (IOException e) {
            System.err.println("Error reading image file: " + e.getMessage());
        }
    }

    private static int getImageWidth(File jpegFile) {
        try {
            BufferedImage image = ImageIO.read(jpegFile);
            return image.getWidth();
        } catch (IOException e) {
            System.err.println("Error reading image file: " + e.getMessage());
        }
        return -1;
    }

    private static int getImageHeight(File jpegFile) {
        try {
            BufferedImage image = ImageIO.read(jpegFile);
            return image.getHeight();
        } catch (IOException e) {
            System.err.println("Error reading image file: " + e.getMessage());
        }
        return -1;
    }
}
