// 计算顶点位置的函数
float4 vert(uint inst : SV_InstanceID,
            uint vertID : SV_VertexID,
            out float4 outPosition : SV_Position,
            inout float4 inPosition : POSITION,
            uniform float4x4 Bones[128],
            uniform float4x4 World : WORLD_MATRIX) {

    float4x4 boneTransform;
    float3 position = inPosition.xyz;
    uint numInfluences = 0;
    float totalWeight = 0;
    float weights[4] = { 0, 0, 0, 0 };
    uint32_t boneIDs[4] = { 0xFFFFFFFF, 0xFFFFFFFF, 0xFFFFFFFF, 0xFFFFFFFF };

    // 遍历每个骨骼，计算影响
    for (uint i = 0; i < 4; ++i) {
        if (weights[i] > 0) {
            boneTransform += Bones[boneIDs[i]] * weights[i];
            totalWeight += weights[i];
        }
    }

    position = mul(position, boneTransform);
    outPosition = mul(World, float4(position, 1.0));

    return inPosition;
}
