以FFmpeg.AutoGen为例，以下是使用C#代码提取视频帧，对帧进行处理，再将处理后的帧保存回视频中的步骤：

1. 提取视频帧

```csharp
using System;
using FFmpeg.AutoGen;

namespace FFmpegNetTest
{
    class Program
    {
        static unsafe void Main(string[] args)
        {
            // 注册FFmpeg.dll
            ffmpeg.av_register_all();

            // 打开输入文件
            AVFormatContext* inputContext = null;
            var ret = ffmpeg.avformat_open_input(&inputContext, "input.mp4", null, null);

            // 查找流信息
            ret = ffmpeg.avformat_find_stream_info(inputContext, null);

            // 查找视频流
            AVStream* videoStream = null;
            int videoStreamIndex = -1;
            for (var i = 0; i < inputContext->nb_streams; i++)
            {
                var stream = inputContext->streams[i];
                if (stream->codec->codec_type == AVMediaType.AVMEDIA_TYPE_VIDEO)
                {
                    videoStream = stream;
                    videoStreamIndex = i;
                    break;
                }
            }

            // 创建输出文件，设置编码器和像素格式
            var outputFormat = ffmpeg.av_guess_format(null, "output.mp4", null);
            AVFormatContext* outputContext = null;
            ffmpeg.avformat_alloc_output_context2(&outputContext, outputFormat, null, "output.mp4");
            AVCodecID codecId = ffmpeg.av_guess_codec(outputFormat, null, null, null, AVMediaType.AVMEDIA_TYPE_VIDEO);
            var codec = ffmpeg.avcodec_find_encoder(codecId);
            AVStream* outputVideoStream = ffmpeg.avformat_new_stream(outputContext, codec);
            AVCodecContext* outputCodecContext = outputVideoStream->codec;
            outputCodecContext->width = videoStream->codec->width;
            outputCodecContext->height = videoStream->codec->height;
            outputCodecContext->pix_fmt = videoStream->codec->pix_fmt;
            outputCodecContext->codec_id = codecId;
            outputCodecContext->time_base = videoStream->time_base;
            ffmpeg.avcodec_open2(outputCodecContext, codec, null);

            // 读取帧数据
            var packet = new AVPacket();
            ffmpeg.av_init_packet(&packet);
            var frame = ffmpeg.av_frame_alloc();
            while (ffmpeg.av_read_frame(inputContext, &packet) == 0)
            {
                // 是否为视频流
                if (packet.stream_index != videoStreamIndex)
                {
                    continue;
                }

                // 解码帧
                int gotFrame;
                ffmpeg.avcodec_decode_video2(videoStream->codec, frame, &gotFrame, &packet);

                if (gotFrame != 0)
                {
                    // 将帧数据保存为图片
                    SaveFrame(videoStream->codec, frame, "image.png");

                    // 修改帧数据

                    // 写入输出文件
                    ffmpeg.av_interleaved_write_frame(outputContext, &packet);
                }

                ffmpeg.av_packet_unref(&packet);
            }

            // 写入输出文件尾部信息
            ffmpeg.av_write_trailer(outputContext);

            // 释放资源
            ffmpeg.av_frame_free(&frame);
            ffmpeg.avformat_close_input(&inputContext);
            ffmpeg.avformat_free_context(inputContext);
            ffmpeg.avcodec_close(outputCodecContext);
            ffmpeg.avio_close(outputContext->pb);
            ffmpeg.avformat_free_context(outputContext);
        }
    }

    static unsafe void SaveFrame(AVCodecContext* codecContext, AVFrame* frame, string filename)
    {
        var imageFormat = GetPixelFormat(codecContext->pix_fmt);
        using (var bitmap = new System.Drawing.Bitmap(frame->width, frame->height, imageFormat))
        {
            var bitmapData = bitmap.LockBits(new System.Drawing.Rectangle(0, 0, frame->width, frame->height), System.Drawing.Imaging.ImageLockMode.WriteOnly, imageFormat);
            var src = new IntPtr(frame->data[0]);
            for (var y = 0; y < frame->height; y++)
            {
                var dst = new IntPtr(bitmapData.Scan0.ToInt64() + y * bitmapData.Stride);
                Utilities.CopyMemory(dst, src, frame->width * 4);
                src += frame->linesize[0];
            }
            bitmap.UnlockBits(bitmapData);
            bitmap.Save(filename);
        }
    }

    static System.Drawing.Imaging.PixelFormat GetPixelFormat(AVPixelFormat pixelFormat)
    {
        switch (pixelFormat)
        {
            case AVPixelFormat.AV_PIX_FMT_BGR24:
                return System.Drawing.Imaging.PixelFormat.Format24bppRgb;
            case AVPixelFormat.AV_PIX_FMT_RGB24:
                return System.Drawing.Imaging.PixelFormat.Format24bppRgb;
            case AVPixelFormat.AV_PIX_FMT_GRAY8:
                return System.Drawing.Imaging.PixelFormat.Format8bppIndexed;
            case AVPixelFormat.AV_PIX_FMT_RGBA:
                return System.Drawing.Imaging.PixelFormat.Format32bppArgb;
            case AVPixelFormat.AV_PIX_FMT_BGRA:
                return System.Drawing.Imaging.PixelFormat.Format32bppArgb;
            default:
                throw new NotSupportedException();
        }
    }
}
