    public List<Message> getLatestMessages(String host, int port, String scheme, String username, String password) throws IOException {
        RestHighLevelClient restHighLevelClient = new RestHighLevelClient(RestClient.builder(new HttpHost(host, port, scheme))
                .setHttpClientConfigCallback(httpAsyncClientBuilder -> {
                    httpAsyncClientBuilder.disableAuthCaching();
                    if (username != null && password != null) {
                        return httpAsyncClientBuilder.setDefaultCredentialsProvider(() -> {
                            return new BasicCredentialsProvider().setCredentials(AuthScope.ANY,
                                    new UsernamePasswordCredentials(username, password));
                        });
                    } else {
                        return httpAsyncClientBuilder;
                    }
                }));

        SearchRequest searchRequest = new SearchRequest("_all");
        SearchSourceBuilder searchSourceBuilder = new SearchSourceBuilder();
        searchSourceBuilder.query(QueryBuilders.matchAllQuery());
        searchSourceBuilder.size(SIZE);
        searchSourceBuilder.sort("timestamp");
        searchRequest.source(searchSourceBuilder);

        SearchResponse searchResponse = restHighLevelClient.search(searchRequest, RequestOptions.DEFAULT);
        SearchHit[] hits = searchResponse.getHits().getHits();

        List<Message> messages = new ArrayList<>();
        Arrays.stream(hits).forEach(hit -> {
            Message message = JacksonUtils.fromJsonString(hit.getSourceAsString(), Message.class);
            messages.add(message);
        });

        return messages;
    }

