#define SCK 15

// Пины кнопок
#define BUTTON_CONNECT 6
#define BUTTON_CREATE 7
#define BUTTON_DELETE 8

void setup() {
  Serial.begin(115200);

  pinMode(SD_CS, OUTPUT);
  digitalWrite(SD_CS, HIGH);

  pinMode(BUTTON_CONNECT, INPUT_PULLUP);
  pinMode(BUTTON_CREATE, INPUT_PULLUP);
  pinMode(BUTTON_DELETE, INPUT_PULLUP);

  SPI.begin(MOSI, MISO, SCK, SD_CS);

  if (!SD.begin(SD_CS)) {
    Serial.println("Error Initializing SD card!");
    while (1);
  }
}

void loop() {
  if (digitalRead(BUTTON_CONNECT) == LOW) {
    digitalWrite(SD_CS, LOW);
    Serial.println("USB Connected!");
    delay(500);
  }

  if (digitalRead(BUTTON_CREATE) == LOW) {
    File myFile = SD.open("test.txt", FILE_WRITE);

    if (myFile) {
      myFile.println("Hello World!");
      myFile.close();
      Serial.println("File created!");
    } else {
      Serial.println("Error creating file!");
    }
    delay(500);
  }

  if (digitalRead(BUTTON_DELETE) == LOW) {
    if (SD.exists("test.txt")) {
      SD.remove("test.txt");
      Serial.println("File deleted!");
    } else {
      Serial.println("File not found!");
    }
    delay(500);
  }
}
