```
const squareDiv = document.createElement("div");
squareDiv.style.width = "50px";
squareDiv.style.height = "50px";
squareDiv.style.borderRadius = "50%";
squareDiv.style.backgroundColor = "black";
squareDiv.style.position = "absolute";
document.body.appendChild(squareDiv);

let targetX = 0;
let targetY = 0;
let currentX = 0;
let currentY = 0;

function update() {
  const diffX = targetX - currentX;
  const diffY = targetY - currentY;
  const distance = Math.sqrt(diffX * diffX + diffY * diffY);
  const speed = distance / 5;
  if (distance < 5) {
    currentX = targetX;
    currentY = targetY;
  } else {
    currentX += speed * diffX / distance;
    currentY += speed * diffY / distance;
  }
  squareDiv.style.left = currentX + "px";
  squareDiv.style.top = currentY + "px";
