namespace NSFW_Detection
{
    class Program
    {
        static void Main(string[] args)
        {
            string imagePath = "test_image.jpg"; // Путь к изображению для анализа
            string modelPath = "nsfw_model.pb"; // Загрузите файл модели из ссылки на GitHub

