        private static keras.Model LoadModel(string modelPath)
        {
            return keras.models.load_model(modelPath);
        }

        private static NDArray PreprocessImage(Bitmap image)
        {
            return ImageDataGenerator.ImageToArray(image).ToArray<float>().Reshape(1, 224, 224, 3);
        }

        private static float[] Predict(keras.Model model, Bitmap image)
        {
            var preprocessedImage = PreprocessImage(image);
            var ndArray = model.predict(preprocessedImage, batch_size: 1);
            return ndArray.Arr<float>();
        }

        private static void PrintPrediction(float[] prediction)
        {
            Console.WriteLine("Drawings: {0:0.00}%", prediction[0] * 100);
            Console.WriteLine("Hentai:   {0:0.00}%", prediction[1] * 100);
