    public static class ImageProcessing
    {
        public static Bitmap LoadAndResizeImage(string imagePath, int targetSize = 224, bool grayscale = false)
        {
            Bitmap originalImg = new Bitmap(imagePath);
            Bitmap resizedImg = new Bitmap(targetSize, targetSize);
            using (Graphics graphic = Graphics.FromImage(resizedImg))
            {
                graphic.InterpolationMode = System.Drawing.Drawing2D.InterpolationMode.HighQualityBicubic;
                graphic.DrawImage(originalImg, 0, 0, targetSize, targetSize);
            }
            return resizedImg;
        }
    }
}
