let mouseDownEvent = document.createEvent("MouseEvents");
mouseDownEvent.initMouseEvent(
    "mousedown",
    true,
    true,
    window,
    0,
    startX,
    startY,
    startX,
    startY,
    false,
    false,
    false,
    false,
    0,
    null
);
element.dispatchEvent(mouseDownEvent);

// 模拟鼠标拖动事件
let mouseMoveEvent = document.createEvent("MouseEvents");
mouseMoveEvent.initMouseEvent(
    "mousemove",
    true,
    true,
    window,
    0,
    startX,
    startY + 10,
    startX + 10,
    startY,
    false,
    false,
    false,
    false,
    0,
    null
);
let startXOffset = 10; // 鼠标拖动时 X 坐标增量
let interval = setInterval(() => {
    startX += startXOffset;
    mouseMoveEvent.clientX = startX;
    element.dispatchEvent(mouseMoveEvent);
    if (startX >= endX) clearInterval(interval); // 达到拖动距离后停止拖动
}, 10);

// 模拟鼠标松开事件
let mouseUpEvent = document.createEvent("MouseEvents");
mouseUpEvent.initMouseEvent(
    "mouseup",
    true,
    true,
    window,
    0,
    endX,
    endY,
    endX,
    endY,
    false,
    false,
    false,
    false,
    0,
    null
);
setTimeout(() => {
    element.dispatchEvent(mouseUpEvent);
}, duration);
```

