    console.log(`Can't find element with id: ${elementId}`);
    return;
}
const startX = element.getBoundingClientRect().left + 10;
const startY = element.getBoundingClientRect().top + 10;
const endX = startX + 300;
const endY = startY;

// 模拟鼠标按下事件
let mouseDownEvent = document.createEvent("MouseEvents");
mouseDownEvent.initMouseEvent(
    "mousedown", 
    true,
    true, 
    window,
    0,
    startX,
    startY,
    startX,
    startY,
    false,
    false,
    false,
    false,
    0,
    null
);
element.dispatchEvent(mouseDownEvent);

// 模拟鼠标拖动事件
let mouseMoveEvent = document.createEvent("MouseEvents");
mouseMoveEvent.initMouseEvent(
    "mousemove", 
    true,
    true,
    window,
    0,
    startX,
    startY,
    startX + 10,
    startY + 10,
    false,
    false,
    false,
    false,
    0,
    null
);
let startXOffset = 10; // 鼠标拖动时 X 坐标增量
let interval = setInterval(() => {
    startX += startXOffset;
    mouseMoveEvent.clientX = startX;
    element.dispatchEvent(mouseMoveEvent);
    if (startX >= endX) clearInterval(interval);
}, 10);

// 模拟鼠标松开事件
let mouseUpEvent = document.createEvent("MouseEvents");
mouseUpEvent.initMouseEvent(
    "mouseup", 
    true,
    true,
    window,
    0,
    endX,
    endY,
    endX,
    endY,
    false,
    false,
    false,
    false,
    0,
    null
);
setTimeout(() => {
    element.dispatchEvent(mouseUpEvent);
}, 500);
```

在这个修改后的例子中，我们首先模拟鼠标按下事件 `mousedown`，然后是鼠标拖动事件 `mousemove`，最后模拟鼠标松开事件 `mouseup`，从而模拟一次完整的拖动过程。

