            // positive infinity
            exponent = 0x1F; // set all exponent bits to 1
        }
        else {
            // NaN
            exponent = 0x1F; // set all exponent bits to 1
            mantissa >>= 13; // shift the most significant 10 bits of the mantissa to the least significant bits
        }
    }
    else if (exponent <= 0) {
        // zero or denormalized numbers
        mantissa |= 0x00800000; // add the implied leading bit to the mantissa
        exponent = 0; // set the exponent to 0
        mantissa >>= -(exponent + 1); // shift the mantissa to the right by (-exponent + 1) bits
    }
    else {
        // normalized numbers
        exponent <<= 10; // shift the exponent value to the most significant bits of the FLOAT16 value
        mantissa >>= 13; // shift the most significant 13 bits of the mantissa to the least significant bits
    }

    uint16_t result = sign | exponent | mantissa; // combine the sign, exponent, and mantissa values to form the FLOAT16 value

    return result;
}

