Manager::Manager(const YAML::Node& config, const rclcpp::NodeOptions& options) : Node("lidar_manager", options)
{
  RCLCPP_INFO(this->get_logger(), "Manager yaml constructor");
  YAML::Node lidar_config_list_yaml = yamlSubNodeAbort(config, "lidar");
  for (auto lidar_config : lidar_config_list_yaml)
  {
    const auto driver_cfg_yaml = yamlSubNodeAbort(lidar_config, "driver");
    const auto ros_cfg_yaml = yamlSubNodeAbort(lidar_config, "ros");

    robosense::lidar::RSDriverParam driver_cfg;
    RosSenderParam ros_cfg;

    std::string lidar_type_str;
    yamlReadAbort<std::string>(driver_cfg_yaml, "lidar_type", lidar_type_str);
    driver_cfg.lidar_type = robosense::lidar::strToLidarType(lidar_type_str);
    yamlRead<uint16_t>(driver_cfg_yaml, "msop_port", driver_cfg.input_param.msop_port, 6699);
    yamlRead<uint16_t>(driver_cfg_yaml, "difop_port", driver_cfg.input_param.difop_port, 7788);
    yamlRead<float>(driver_cfg_yaml, "min_distance", driver_cfg.decoder_param.min_distance, 0.2);
    yamlRead<float>(driver_cfg_yaml, "max_distance", driver_cfg.decoder_param.max_distance, 200);
    yamlRead<float>(driver_cfg_yaml, "start_angle", driver_cfg.decoder_param.start_angle, 0);
    yamlRead<float>(driver_cfg_yaml, "end_angle", driver_cfg.decoder_param.end_angle, 360);
    yamlRead<std::string>(driver_cfg_yaml, "frame_id", driver_cfg.frame_id, "rslidar");

    ros_cfg.frame_id = driver_cfg.frame_id;
    yamlRead<std::string>(ros_cfg_yaml, "ros_send_point_cloud_topic", ros_cfg.topic_name, "rslidar_points");

    std::string compression_type;
    yamlRead<std::string>(ros_cfg_yaml, "compressed_res", compression_type, "");
    pcl::io::compression_Profiles_e compressed_arg;
    if (compression_type == "LOW_RES")
    {
      compressed_arg = pcl::io::LOW_RES_ONLINE_COMPRESSION_WITHOUT_COLOR;
    }
    else if (compression_type == "HIGH_RES")
    {
      compressed_arg = pcl::io::HIGH_RES_ONLINE_COMPRESSION_WITHOUT_COLOR;
    }
    else if (compression_type == "MED_RES")
    {
      compressed_arg = pcl::io::MED_RES_ONLINE_COMPRESSION_WITHOUT_COLOR;
    }
    else
    {
      compressed_arg = pcl::io::LOW_RES_ONLINE_COMPRESSION_WITHOUT_COLOR;
      spdlog::error("compressed_arg is not valid, use default value LOW_RES");
    }

    // 构建发送接受

    auto receiver = std::make_shared<LidarReceiver>(driver_cfg, *this);
    receivers_.push_back(receiver);

    auto sender = std::make_shared<RosSender>(ros_cfg, *this);
    sender->Connect(receiver.get());
    senders_.push_back(sender);

    auto compressed_sender = std::make_shared<RosPclCompressSender>(ros_cfg, *this, compressed_arg);
    compressed_sender->Connect(receiver.get());
    senders_.push_back(compressed_sender);
  }

  start_receive();
}

