function [ucost, ttake] = takeoff(ptake, ftake, materials)
    % Calculate unit cost
    ucost = sum(ptake(:,1:end-1))/sum(ptake(:,end));

    % Check if there is enough data to calculate unit cost
    if size(ptake,1) < 2
        ttake = "Info is not enough";
        return;
    end

    % Calculate total cost for future months
    fcost = ucost * sum(ftake, 2);

    % Create ttake cell array
    ttake = cell(length(materials)+1, size(ptake,1)+size(ftake,1));

    % Fill in material names
    ttake(1,:) = ["Material" materials "cost"];

    % Fill in past months data
    for i = 1:size(ptake,1)
        ttake{1,i+1} = "Month" + i;
        for j = 1:length(materials)
            ttake{j+1,i+1} = ptake(i,j);
        end
        ttake{end,i+1} = ptake(i,end);
    end

    % Fill in future months data and total cost
