namespace Assignment_4
{
    public class Item : IComparable<Item>
    {
        /* Properties */

        public string Name { get; set; }
        public int GoldPieces { get; set; }
        public double Weight { get; set; }

        /* Constructor */

        /// <summary>
        /// Constructor, initializes properties for Name, GoldPieces, Weight.
        /// </summary>
        /// <param name="Name"></param>
        /// <param name="GoldPieces"></param>
        /// <param name="Weight"></param>
        public Item(string name, int goldPieces, double weight)
        {
            this.Name = name;
            this.GoldPieces = goldPieces;
            this.Weight = weight;
        }

        /* Methods */

        /// <summary>
        /// Checks if it’s an instance of the Item class, and compares its properties to the current Item object’s properties. 
        /// </summary>
        /// <param name="obj"></param>
        /// <returns></returns>
        public override bool Equals(object obj)
        {
            if (obj == null || GetType() != obj.GetType()) // Null and data type check
            {
                return false;
            }

            Item itemObj = (Item)obj; // Store obj as a item type

            return this.Name == itemObj.Name && this.GoldPieces == itemObj.GoldPieces && this.Weight == itemObj.Weight; // Property value check
        }

        /// <summary>
        /// Accepts an Item object as a parameter and compares the Name property of the two Item objects.
        /// </summary>
        /// <param name="other"></param>
        /// <returns></returns>
        public int CompareTo(Item itemObj)
        {
            return this.Name.CompareTo(itemObj.Name);
        }

        /// <summary>
        /// Specific formatting for string output.
        /// </summary>
        /// <returns></returns>
        public override string ToString()
        {
            return $"{this.Name} is worth {this.GoldPieces}gp and weighs {this.Weight}kg";
        }
    }
}


using System;
using System.Collections.Generic;
using System.IO;
using System.Text;

namespace Assignment_4
{
    public class Adventure
    {
        private HashMap<StringKey, Item> map;

        public HashMap<StringKey, Item> GetMap()
        {
            return map;
        }

        public Adventure(string fileName)
        {
            if (fileName == null) throw new ArgumentNullException();

            try
            {
                string[] lines = File.ReadAllLines(fileName);
                map = new HashMap<StringKey, Item>();
                foreach (var line in lines)
                {
                    string[] properties = line.Split(',');
                    string name = properties[0];
                    int gold = Convert.ToInt32(properties[1]);
                    double weight = Convert.ToDouble(properties[2]);
                    map.Put(new StringKey(name), new Item(name, gold, weight));
                }
            }
            catch
            {
                throw new ArgumentException();
            }
        }


        public string PrintLootMap()
        {
            StringBuilder result = new StringBuilder();

            List<Item> list = new List<Item>();
            IEnumerator<Item> items = map.Values();

            while (items.MoveNext())
            {
                if (items.Current.GoldPieces > 0)
                {
                    list.Add(items.Current);
                }
            }

            list.Sort();

            foreach (var item in list)
            {
                result.Append($"{item}\n");
            }

            return result.ToString();
        }
    }
}

