using UnityEngine;
using MalbersAnimations.Events;
using System.Collections.Generic;

namespace MalbersAnimations
{
    [AddComponentMenu("Malbers/Weapons/MInventory Basic")]
    public class MInventory : MonoBehaviour 
    {
        public List<GameObject> Inventory;
        public GameObjectEvent OnEquipItem;

        [SerializeField]
        private ItemCollection m_ItemCollection; // Referencia a la instancia de la clase ItemCollection

        public virtual void EquipItem(int Slot)
        {
            if (Slot < Inventory.Count) { 
                GameObject itemGO = Inventory[Slot];
                Item item = m_ItemCollection.FirstOrDefault(x => x.Name == itemGO.name);
                if(item != null) { 
                    OnEquipItem.Invoke(itemGO);
                    // Aquí se puede hacer cualquier cosa con el objeto "item", como equiparlo o modificar sus propiedades.
                }
            }
        }

        public virtual void AddItem(GameObject item) {
            m_ItemCollection.Add(item.GetComponent<Item>());
            Inventory.Add(item);
        }
    }
}
