```csharp
using System;
using System.Data;
using System.Data.SqlClient;

namespace WarehouseManagementSystem
{
    class Product
    {
        public void AddProduct(string productName, string description, decimal price, int quantity)
        {
            using (SqlConnection connection = Database.Connect())
            {
                string query = "INSERT INTO Product (ProductName, Description, Price, Quantity) VALUES (@ProductName, @Description, @Price, @Quantity)";
                SqlCommand command = new SqlCommand(query, connection);
                command.Parameters.AddWithValue("@ProductName", productName);
                command.Parameters.AddWithValue("@Description", description);
                command.Parameters.AddWithValue("@Price", price);
                command.Parameters.AddWithValue("@Quantity", quantity);
                command.ExecuteNonQuery();
            }
        }

        public void DeleteProduct(int productId)
        {
            using (SqlConnection connection = Database.Connect())
            {
                string query = "DELETE FROM Product WHERE ProductID=@ProductID";
                SqlCommand command = new SqlCommand(query, connection);
                command.Parameters.AddWithValue("@ProductID", productId);
                command.ExecuteNonQuery();
            }
        }

        public void UpdateProduct(int productId, string productName, string description, decimal price, int quantity)
        {
            using (SqlConnection connection = Database.Connect())
            {
                string query = "UPDATE Product SET ProductName=@ProductName, Description=@Description, Price=@Price, Quantity=@Quantity WHERE ProductID=@ProductID";
                SqlCommand command = new SqlCommand(query, connection);
                command.Parameters.AddWithValue("@ProductID", productId);
                command.Parameters.AddWithValue("@ProductName", productName);
                command.Parameters.AddWithValue("@Description", description);
                command.Parameters.AddWithValue("@Price", price);
                command.Parameters.AddWithValue("@Quantity", quantity);
                command.ExecuteNonQuery();
            }
        }

        public DataTable ListProducts()
        {
            using (SqlConnection connection = Database.Connect())
            {
                string query = "SELECT ProductID, ProductName, Description, Price, Quantity FROM Product";
                SqlDataAdapter adapter = new SqlDataAdapter(query, connection);
                DataTable dataTable = new DataTable();
                adapter.Fill(dataTable);
                return dataTable;
            }
        }

        public DataTable SearchProducts(string productName)
        {
            using (SqlConnection connection = Database.Connect())
            {
                string query = "SELECT ProductID, ProductName, Description, Price, Quantity FROM Product WHERE ProductName LIKE @ProductName";
                SqlDataAdapter adapter = new SqlDataAdapter(query, connection);
                adapter.SelectCommand.Parameters.AddWithValue("@ProductName", "%" + productName + "%");
                DataTable dataTable = new DataTable();
                adapter.Fill(dataTable);
                return dataTable;
            }
        }
    }
}
