    class Purchase
    {
        public void AddPurchase(int productId, string productName, int quantity, decimal price, DateTime purchaseDate)
        {
            using (SqlConnection connection = Database.Connect())
            {
                string query = "INSERT INTO Purchase (ProductID, ProductName, Quantity, Price, PurchaseDate) VALUES (@ProductID, @ProductName, @Quantity, @Price, @PurchaseDate)";
                SqlCommand command = new SqlCommand(query, connection);
                command.Parameters.AddWithValue("@ProductID", productId);
                command.Parameters.AddWithValue("@ProductName", productName);
                command.Parameters.AddWithValue("@Quantity", quantity);
                command.Parameters.AddWithValue("@Price", price);
                command.Parameters.AddWithValue("@PurchaseDate", purchaseDate);
                command.ExecuteNonQuery();

                query = "UPDATE Product SET Quantity=Quantity+@Quantity WHERE ProductID=@ProductID";
                command = new SqlCommand(query, connection);
                command.Parameters.AddWithValue("@ProductID", productId);
                command.Parameters.AddWithValue("@Quantity", quantity);
                command.ExecuteNonQuery();
            }
        }

        public DataTable SearchPurchases(DateTime startDate, DateTime endDate)
        {
            using (SqlConnection connection = Database.Connect())
            {
                string query = "SELECT PurchaseID, ProductID, ProductName, Quantity, Price, PurchaseDate FROM Purchase WHERE PurchaseDate BETWEEN @StartDate AND @EndDate";
                SqlDataAdapter adapter = new SqlDataAdapter(query, connection);
                adapter.SelectCommand.Parameters.AddWithValue("@StartDate", startDate);
                adapter.SelectCommand.Parameters.AddWithValue("@EndDate", endDate);
                DataTable dataTable = new DataTable();
                adapter.Fill(dataTable);
                return dataTable;
            }
        }
    }
}
