```java
import java.util.*;

class Deonica {
    protected double duzina;
    private double nagib;
    private char oznaka;
    private double ubrzanje;

    public Deonica(double duzina, double nagib) {
        this.duzina = duzina;
        this.nagib = nagib;
        this.oznaka = 'D';
        this.ubrzanje = Math.sin(nagib) * 9.81;
    }

    public char getOznaka() {
        return oznaka;
    }

    public double getUbrzanje() {
        return ubrzanje;
    }

    public double krajnjaBrzina(double pocetnaBrzina) {
        return Math.sqrt(2 * ubrzanje * duzina + pocetnaBrzina * pocetnaBrzina);
    }

    public double vremePretrage(double pocetnaBrzina) {
        return (krajnjaBrzina(pocetnaBrzina) - pocetnaBrzina) / ubrzanje;
    }

    public String toString() {
        return String.format("%c(%.1f,%.1f)", oznaka, duzina, nagib);
    }
}

class TeskaDeonica extends Deonica {
    public TeskaDeonica(double duzina, double nagib) {
        super(duzina, nagib);
        oznaka = 'T';
    }

    public double getUbrzanje() {
        return 9.81 * Math.sin(Math.toRadians(nagib));
    }
}

class Staza {
    private String naziv;
    private ArrayList<Deonica> deonice;

    public Staza(String naziv) {
        this.naziv = naziv;
        this.deonice = new ArrayList<>();
    }

    public void dodajDeonicu(Deonica deonica) {
        deonice.add(deonica);
    }

    public double ukuonaDuzina() {
        double duzinaStaze = 0;
        for (Deonica d : deonice) {
            duzinaStaze += d.duzina;
        }
        return duzinaStaze;
    }

    public double maksimalniNagib() {
        double maksNagib = 0;
        for (Deonica d : deonice) {
            if (d.nagib > maksNagib) {
                maksNagib = d.nagib;
            }
        }
        return maksNagib;
    }

    public char oznakaStaze() throws Exception {
        if (deonice.isEmpty()) throw new Exception("Greska: GOznaka");
        LinkedHashMap<Character, Integer> oznake = new LinkedHashMap<>();
        for (Deonica d : deonice) {
            oznake.put(d.getOznaka(), oznake.getOrDefault(d.getOznaka(), 0) + 1);
        }
        return Collections.max(oznake.entrySet(), Comparator.comparingInt(Map.Entry::getValue)).getKey();
    }

    public double krajnjaBrzina(double pocetnaBrzina) {
        double brzina = pocetnaBrzina;
        for (Deonica d : deonice) {
            brzina = d.krajnjaBrzina(brzina);
        }
        return brzina;
    }

    public double vremePretrage(double pocetnaBrzina) {
        double vreme = 0;
        for (Deonica d : deonice) {
            vreme += d.vremePretrage(pocetnaBrzina);
            pocetnaBrzina = d.krajnjaBrzina(pocetnaBrzina);
        }
        return vreme;
    }

    public String toString() {
        StringBuilder sb = new StringBuilder();
        sb.append(String.format("%s|%d|%.1f|%.1f", naziv, deonice.size(), ukuonaDuzina(), maksimalniNagib()));
        sb.append("\n[");
        for (int i = 0; i < deonice.size(); i++) {
            sb.append(deonice.get(i));
            if (i < deonice.size() - 1) {
                sb.append(",");
            }
        }
        sb.append("]");
