            nal_data = buffer + RTP_HEADER_SIZE + FU_HEADER_SIZE + 1;
            nal_len = nrecv - RTP_HEADER_SIZE - FU_HEADER_SIZE - 1;
        }
    }
    process_nalu(nal_unit_type, nal_data, nal_len);
