const fetchReferrerIncomesStatistics = async (
  token: string,
  from: string,
  to: string,
  referralIds: number[]
) => {
  const promises = referralIds.map((id) =>
    referrerIncomesStatistics(token, from, to, id)
  );
  const results = await Promise.all(promises);

  let combinedData: ReferrerIncomesStatistics = {
    open_link: [],
    registration: [],
    order_pay: [],
    subscription_pay: [],
  };

  results.forEach((result) => {
    if (!result) return;
    result.data.open_link.forEach((entry) => {
      const index = combinedData.open_link.findIndex(
        (e) => e.date === entry.date
      );
      if (index >= 0) {
        combinedData.open_link[index].count += entry.count;
      } else {
        combinedData.open_link.push(entry);
      }
    });
    // Repeat the same logic for registration, order_pay, and subscription_pay
  });

  return combinedData;
};
```

