class XegeraTrucking : Application(), Configuration.Provider {

    override fun onCreate() {
        super.onCreate()
        scheduleLocationUpdate(5)
    }

    fun scheduleLocationUpdate(intervalMinutes: Long) {
        val workRequest = PeriodicWorkRequestBuilder<UpdateLocationWorker>(intervalMinutes, TimeUnit.MINUTES)
            .build()

        WorkManager.getInstance(this).enqueue(workRequest)
    }

    override fun getWorkManagerConfiguration(): Configuration {
        return Configuration.Builder()
            .setWorkerFactory(workerFactory)
            .build()
    }

    @Inject
    lateinit var workerFactory: HiltWorkerFactory
}
