```python
class Node:
    def __init__(self, attribute, split_value, left=None, right=None):
        self.attribute = attribute
        self.split_value = split_value
        self.left = left
        self.right = right

    def decide(self, record):
        if record[self.attribute] < self.split_value:
            return self.left.decide(record)
        else:
            return self.right.decide(record)


class Leaf:
    def __init__(self, label):
        self.label = label

    def decide(self, _):
        return self.label


class DecisionTree:
    def __init__(self, root):
        self.root = root

    def add_node(self, path, split_attribute, split_value):
        current_node = self.root
        for step in path:
            if step == 'L':
                current_node = current_node.left
            else:
                current_node = current_node.right
        current_node.left = Node(split_attribute, split_value)
        current_node.right = Node(split_attribute, split_value)

    def add_leaf(self, path, label):
        current_node = self.root
        for step in path:
            if step == 'L':
                current_node = current_node.left
            else:
                current_node = current_node.right
        current_node.label = label

    def decide(self, record):
        return self.root.decide(record)


def build_decision_tree():
    root = Node('attributeA', 3)
    dt = DecisionTree(root)
    dt.add_leaf('L', 'Result_L1')
    dt.add_node('R', 'attributeB', 5)
    dt.add_leaf('RL', 'Result_L2')
    dt.add_leaf('RR', 'Result_R')
