```swift
func getScoreString(score: Int) -> String {
    var number = score
    var word = "балл"

    if (number % 10 == 1 && number % 100 != 11) {
        word += ""
    } else if (number % 10 >= 2 && number % 10 <= 4 && (number % 100 < 10 || number % 100 >= 20)) {
        word += "a"
    } else {
        word += "ов"
    }

    return "\(number) \(word)"
}

