      </view>
    </view>
    <view class="bottom">
      <!-- #ifdef H5 -->
      <button type="primary" size="default" class="login-btn" @tap="login">
        登录
      </button>
      <!-- #endif -->
      <!-- #ifdef MP-WEIXIN -->
      <button type="primary" size="default" class="login-btn" open-type="getUserInfo" lang="zh_CN"
        @getuserinfo="getUserInfo">
        微信一键登录
      </button>
      <!-- #endif -->
      <view class="d-flex flex-column justify-content-evenly align-items-center text-center"
        style="height: 30vh;">
        <view @tap="xieyi" class="w-100 font-size-base text-color-assist">用户隐私条款</view>
        <view class="font-size-sm text-color-assist">会员权益说明</view>
      </view>
    </view>
  </view>
</template>

<script>
  import {
    saveMember
  } from '@/api/member'
  import {
    Login
  } from '@/api/common.js'
  import {
    mapMutations
  } from 'vuex'
  import storage from '@/utils/storage';
  export default {
    data() {
      return {
        code: ''
      }
    },
    created() {
      // 获取图形验证码
      this.wxlogin();
    },

    methods: {
      ...mapMutations(['SET_MEMBER']),

      async wxlogin() {
        const app = this
        uni.login({
          provider: "weixin",
          success: function(res) {
            console.log(res)
            app.code = res.code
          },
        });
      },
      async getUserInfo(e) {
        console.log(e)
        const app = this
        const {
          errMsg,
          userInfo
        } = e.detail

        if (errMsg !== "getUserInfo:ok") {
          uni.showModal({
            title: '提示',
            content: '您取消了授权登录，请重新授权',
            showCancel: false
          })

          //默认的用户信息
          this.SET_MEMBER(Member)
          uni.navigateBack()

        } else {
          const {
            avatarUrl: avatar,
            city,
            country,
            gender,
            nickName: nickname,
            province
          } = userInfo

          const memberData = Object.assign(userInfo, {
            avatar,
            city,
            country,
            gender,
            nickname,
            province
          })
          saveMember(memberData, app.code).then(result => {
            const member = result.data
            console.log(member, "member")
            this.SET_MEMBER(member)
            this._Login()
            uni.navigateBack()
          });
        }
      },
      _Login() {
        const that = this
        uni.login({
          provider: "weixin",
          success: function(res) {
            Login(res.code).then(result => {
              console.log(result.data)
              that.SET_MEMBER(result.data.member)
              that.myvalue = result.data.member.value
              storage.set("AccessToken", result.data.token)
            }) 
          },
        });
      },
      xieyi() {
        uni.navigateTo({
          url: '/pages/news/index?id=1'
        })
      },
      login() {
        this.SET_MEMBER(Member)
        uni.navigateBack()
      }
    }
  }
</script>

