                        long endSample = startSample + samplesPerFrame;
                        if (endSample >= audioStream[0].Length)
                        {
                            endSample = audioStream[0].Length - 1;
                        }
                        var audioBuffer = new byte[endSample - startSample];
                        int k = 0;
