        {
            var result = new Bitmap(firstImage.Width, firstImage.Height);

            for (int x = 0; x < result.Width; x++)
            {
                for (int y = 0; y < result.Height; y++)
                {
                    var c1 = firstImage.GetPixel(x, y);
                    var c2 = secondImage.GetPixel(x, y);

                    byte r = (byte)(c1.R * alpha + c2.R * (1 - alpha));
                    byte g = (byte)(c1.G * alpha + c2.G * (1 - alpha));
                    byte b = (byte)(c1.B * alpha + c2.B * (1 - alpha));

                    result.SetPixel(x, y, Color.FromArgb(r, g, b));
                }
            }

            return result;
        }

        private Bitmap BlendImages(Bitmap firstImage, Bitmap secondImage, float weight)
        {
            var result = new Bitmap(firstImage.Width, firstImage.Height);

            for (int x = 0; x < result.Width; x++)
            {
                for (int y = 0; y < result.Height; y++)
                {
                    var c1 = firstImage.GetPixel(x, y);
                    var c2 = secondImage.GetPixel(x, y);

                    byte r = (byte)(c1.R * (1 - weight) + c2.R * weight);
                    byte g = (byte)(c1.G * (1 - weight) + c2.G * weight);
                    byte b = (byte)(c1.B * (1 - weight) + c2.B * weight);

                    result.SetPixel(x, y, Color.FromArgb(r, g, b));
                }
            }

            return result;
        }
    }
}
```

以上代码在原有的基础上，增加了对原始音频的读取和写入的处理逻辑，确保生成的视频文件中包含原始音频数据。使用时，可以直接调用AddTransitionEffect方法，并传入所需的参数。

