using System;
using System.Text;

class Program
{
    static void Main(string[] args)
    {
        string alfavit = "АБВГДЕЖЗИЙКЛМНОПРСТУФХЦЧШЩЪЫЬЭЮЯ0123456789";

        Random rnd = new Random();

        for (int i = 0; i < 10; i++)
        {
            int length = rnd.Next(3, 16);
            StringBuilder password = new StringBuilder();

            for (int j = 0; j < length; j++)
