    {
        SqlConnection connection = new SqlConnection("Data Source=SERVERNAME;Initial Catalog=DATABASENAME;User ID=USERNAME;Password=PASSWORD;");
        // Replace SERVERNAME, DATABASENAME, USERNAME, and PASSWORD with the appropriate values for your database connection
        SqlCommand command;
        SqlDataReader reader;
        DataTable dataTable;

        public Form1()
        {
            InitializeComponent();
        }

